CREATE TABLE IF NOT EXISTS npc_dialogue_text (id BIGINT NOT NULL AUTO_INCREMENT PRIMARY KEY,npc_file VARCHAR(255) NOT NULL,call_type VARCHAR(32) NOT NULL,line_number INT NOT NULL,column_number INT NOT NULL,raw_text LONGTEXT NOT NULL,string_literal LONGTEXT NOT NULL,ast_context LONGTEXT,function_name VARCHAR(128),associated_quest_id INT NULL,associated_quest_ids_json JSON NULL,ast_marker VARCHAR(255) NOT NULL,created_at TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,updated_at TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,UNIQUE KEY uk_npc_dialogue_text_marker (npc_file, ast_marker),KEY idx_npc_dialogue_file (npc_file),KEY idx_npc_dialogue_quest (associated_quest_id),KEY idx_npc_dialogue_call (call_type)) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci
